function scrollToNextAts(elem){
var sibling = elem.nextElementSibling;
while (sibling) {
if (sibling.className == "ats-script"){
sibling.scrollIntoView();
return;
};
sibling = sibling.nextElementSibling;
}
};

function scrollToPrevAts(elem){
var sibling = elem.previousElementSibling;
while (sibling) {
if (sibling.className == "ats-script"){
sibling.scrollIntoView();
return;
};
sibling = sibling.previousElementSibling;
}
};

function openModal(vVar) {
console.log(vVar);
document.getElementById("myModal").style.display = "block";
var image = document.getElementById(vVar);
var divImage = document.getElementById("imgCont");
var modalImage = document.getElementById("modal_image");

modalImage.src = image.src;
divImage.style.display = "block";
};

function openModalVid(vVar) {
console.log(vVar);
document.getElementById("myModalVid").style.display = "block";
var vid = document.getElementById(vVar);
var divVideo = document.getElementById("vidCont");
var modalVideo = document.getElementById("modal_video");
modalVideo.setAttribute("controls","controls");
modalVideo.src = vid.src;
divVideo.style.display = "block";
};

function closeModal() {
document.getElementById("myModal").style.display = "none";
document.getElementById("myModalVid").style.display = "none";
};

window.addEventListener('load', function () {
let videoDiv = document.querySelectorAll('.video');
Array.prototype.forEach.call(videoDiv, function(element) {
    element.setAttribute("controls","controls");
});
});
